module Advent6(main) where

import Data.List
input1 :: String
input1 = ""

indexed :: [a] -> [(Int, a)]
indexed = zip [0..]

windows :: Int -> [a] -> [[a]]
windows _ [] = []
windows n xs = take n xs : windows n (tail xs)

uniqueChars :: String -> Bool
uniqueChars s = length s == length (nub s)


main :: IO ()
main = do
  let n = 4
  let indexedWindows = indexed $ windows n input1
  let firstUniqueWindow = head $ filter (uniqueChars . snd) indexedWindows
  print (fst firstUniqueWindow + n)
  print (snd firstUniqueWindow)
  let k = 14
  let indexedMessageWindows = indexed $ windows k input1
  let firstUniqueMessage = head $ filter (uniqueChars . snd) indexedMessageWindows
  print (fst firstUniqueMessage + k)
  print (snd firstUniqueMessage)